    static let shared = Recipes()

    func findRecipe(recipeName: String) -> Recipe {
        var item : Recipe?
        for recipe in uzbekMeals {
            if recipeName == recipe.title {
                item = recipe
            }
        }
        for recipe in westernMeals {
            if recipeName == recipe.title {
                item = recipe
            }
        }
        for recipe in salads {
            if recipeName == recipe.title {
                item = recipe
            }
        }
        for recipe in desserts {
            if recipeName == recipe.title {
                item = recipe
            }
        }
        return item!
    }
    
    let uzbekMeals: [Recipe] = [
      
        Recipe(title: "Palov", imageName:"palov", serving: 5, preparationTime: 60, info: "Palov is a type of rice dish that is popular in Uzbek cuisine. It is a one-pot meal made with rice, meat (usually lamb or beef), and a mixture of vegetables, such as carrots, onions, and potatoes. Spices, such as cumin, coriander, and turmeric, are added to the dish to give it a unique flavor. The ingredients are cooked together in a large pot until the rice is tender and the flavors are well-combined.", ingredients: [Ingredients(name: "Rice", amount: "700g" ),Ingredients(name: "Beef/lamb", amount: "500g" ),Ingredients(name: "Carrot", amount: "4" ), Ingredients(name: "Chickpeas", amount: "200g" ),Ingredients(name: "Onion", amount: "1" ), Ingredients(name: "Cumin seeds", amount: "1 tablespoon" ), Ingredients(name: "Garlic", amount: "1" ), Ingredients(name: "Salt", amount: "1 teaspoon" ), Ingredients(name: "Vegetable oil", amount: "300ml")], cookingGuide: [CookingGuide(step: 1, guide: "In a large, heavy-bottomed pot, heat the oil over medium heat. Add the onion and cook until soft and translucent, about 5 minutes."), CookingGuide(step: 2, guide: "Add the lamb or beef and cook until browned on all sides, about 10 minutes."), CookingGuide(step: 3, guide: "Stir in the rice, carrots, and potato and cook for 2-3 minutes until the rice is lightly toasted."), CookingGuide(step: 4, guide: "Add the cumin seeds, paprika, turmeric, black pepper, and salt and cook for another minute, stirring constantly."), CookingGuide(step: 5, guide: "Pour in the boiling water and add the chickpeas, if using. Stir everything together, cover the pot, and reduce the heat to low."), CookingGuide(step: 6, guide: "Cook the palov for 25-30 minutes, or until the rice is tender and all of the liquid has been absorbed. Stir occasionally to prevent the rice from sticking to the bottom of the pot."), CookingGuide(step: 7, guide: "Serve the palov hot, garnished with fresh cilantro, if desired.  Enjoy your delicious and authentic Uzbek Palov!")]),



   Recipe(title: "Mastava", imageName:"mastava", serving: 4, preparationTime: 40, info: "Mastava is a traditional dish in Central Asian cuisine, specifically in the Uzbek culinary traditions. It is a hearty stew made with meat (typically beef or lamb), potatoes, onions, and seasonings. It is typically served with bread, rice, or noodles and is considered a comfort food in the region. The dish is made by cooking the ingredients in a large pot until the flavors are well-combined and the meat is tender. Mastava is often served as a main dish for special occasions or as a warming meal during the colder months.",
