   let westernMeals : [Recipe] = [
       Recipe(title: "Pizza", imageName: "pizza", serving: 2, preparationTime: 80, info: "Pizza is a popular Italian dish that has become well-known and loved worldwide. It typically consists of a round, flattened bread base topped with various ingredients, including tomato sauce, cheese, and a variety of toppings.", ingredients: [Ingredients(name: "Egg", amount: "1" ),Ingredients(name: "Flour", amount: "300g" ),Ingredients(name: "Cream", amount: "100g" ), Ingredients(name: "Sugar", amount: "1 tablespoon" ),Ingredients(name: "Salt", amount: "1 teaspoon" ), Ingredients(name: "Yeist", amount: "1 tablespoon" ), Ingredients(name: "Tomato", amount: "3" ), Ingredients(name: "Onion", amount: "1"), Ingredients(name: "Olive oil", amount: "100ml"), Ingredients(name: "Oregano", amount: "as one needs"), Ingredients(name: "Garlic", amount: "2 cloves"), Ingredients(name: "Mozarellaa", amount: "200g"), Ingredients(name: "Sausage", amount: "100g"), Ingredients(name: "Shampinion", amount: "10"), Ingredients(name: "Olive beans", amount: "10 dona")],
              cookingGuide: [CookingGuide(step: 1, guide: "Preheat the oven: Set your oven to the recommended temperature for baking pizza. Typically, around 475°F (245°C) is a good temperature for homemade pizza."), CookingGuide(step: 2, guide: "Prepare the dough: If you're using store-bought dough, follow the instructions on the packaging. If making dough from scratch, combine flour, water, yeast, and salt in a mixing bowl. Knead the dough until it forms a smooth and elastic ball. Let it rest in a warm place, covered with a damp cloth, for about an hour until it doubles in size."), CookingGuide(step: 3, guide: "Roll out the dough: On a clean, floured surface, place the dough and use a rolling pin to roll it out into your desired pizza shape and thickness. If you prefer a thin crust, roll it thinner; for a thicker crust, leave it slightly thicker."), CookingGuide(step: 4, guide: "Prepare the pizza base: Transfer the rolled-out dough onto a baking sheet or pizza stone. If using a baking sheet, lightly grease it or use parchment paper to prevent sticking. If using a pizza stone, dust it with flour or cornmeal to prevent sticking."), CookingGuide(step: 5, guide: "Spread the tomato sauce: Using a spoon or ladle, spread a thin and even layer of tomato sauce over the dough, leaving a small border around the edges for the crust."),CookingGuide(step: 6, guide: "Add cheese and toppings: Sprinkle a generous amount of cheese evenly over the tomato sauce. Then add your desired toppings, distributing them evenly across the pizza. Be creative and mix and match toppings to your preference."),CookingGuide(step: 7, guide: "Drizzle with olive oil: Lightly drizzle olive oil over the toppings to add flavor and aid in browning."),CookingGuide(step: 8, guide: "Season with salt: Sprinkle a pinch of salt over the pizza to enhance the flavors."),CookingGuide(step: 9, guide: "Bake the pizza: Place the pizza in the preheated oven and bake for about 12-15 minutes or until the crust is golden brown, and the cheese is melted and bubbly. Keep an eye on it to prevent burning."), CookingGuide(step: 10, guide: "Remove from the oven: Carefully take the pizza out of the oven using oven mitts or a pizza peel. Allow it to cool for a few minutes before slicing."), CookingGuide(step: 11, guide: "Serve and enjoy: Cut the pizza into slices and serve it hot. You can garnish with fresh herbs like basil or oregano, or even a sprinkle of grated Parmesan cheese if desired."), CookingGuide(step: 12, guide: "Remember, these instructions are a general guideline, and you can adjust them based on your preferred crust thickness, toppings, and oven temperature. Enjoy your homemade pizza!")]),
       
       
       Recipe(title: "Fajita", imageName:"fajita", serving: 5, preparationTime: 30, info: "Fajitas are a popular Tex-Mex dish that is known for its sizzling presentation and flavorful combination of grilled meats, vegetables, and various toppings. The term 'fajita' originally referred to a cut of beef called skirt steak, which was traditionally used in the dish. However, it has evolved to include other types of meat such as chicken, shrimp, or even vegetarian options like grilled vegetables or tofu.", ingredients: [Ingredients(name: "Onion", amount: "200 g" ),Ingredients(name: "Chicken", amount: "500g" ),Ingredients(name: "Paprika", amount: "3-4" ), Ingredients(name: "Tomato", amount: "200g" ),Ingredients(name: "Pepper", amount: "2 teaspoon" ), Ingredients(name: "Coriander", amount: "0.5 teaspoon" ), Ingredients(name: "Black pepper", amount: "0.5 teaspoon") , Ingredients(name: "Garlic", amount: "3 cloves" ), Ingredients(name: "Cumin", amount: "0.5 teaspoon" ), Ingredients(name: "Olive oil", amount: "3 table spoons"), Ingredients(name: "Greens", amount: "a few leaves"), Ingredients(name: "Pita/bread", amount: "10 pieces")], cookingGuide: [CookingGuide(step: 1, guide: "Prepare the marinade: In a bowl, whisk together olive oil, minced garlic, lime juice, chili powder, cumin powder, paprika, salt, and pepper. This will be used to marinate the chicken and vegetables."), CookingGuide(step: 2, guide: "Marinate the chicken: Place the sliced chicken in a shallow dish or a resealable plastic bag. Pour half of the marinade over the chicken, ensuring it is well coated. Reserve the remaining marinade for the vegetables. Allow the chicken to marinate for at least 30 minutes or up to 4 hours in the refrigerator."), CookingGuide(step: 3, guide: "Preheat a skillet or grill pan: Heat a skillet or grill pan over medium-high heat. You can also use an outdoor grill if available."), CookingGuide(step: 4, guide: "Cook the chicken: Remove the chicken from the marinade and discard any excess marinade. Place the chicken in the preheated skillet or grill pan and cook for about 5-7 minutes per side or until cooked through and slightly charred. Transfer the cooked chicken to a plate and cover it with foil to keep it warm."), CookingGuide(step: 5, guide: "Cook the vegetables: In the same skillet or grill pan, add the sliced bell peppers and onion. Pour the reserved marinade over the vegetables and cook for about 5-7 minutes, stirring occasionally, until the vegetables are tender and slightly charred."), CookingGuide(step: 6, guide: "Warm the tortillas: While the vegetables are cooking, you can warm the tortillas. Wrap the tortillas in aluminum foil and place them in a preheated oven at 350°F (175°C) for about 5 minutes or until heated through. Alternatively, you can warm them directly on a dry skillet or griddle for a minute or two on each side."), CookingGuide(step: 7, guide: "Assemble the fajitas: Slice the cooked chicken into thin strips. Fill each warmed tortilla with a portion of the cooked chicken and grilled vegetables. Add any desired toppings such as shredded cheese, guacamole, salsa, sour cream, or fresh cilantro."), CookingGuide(step: 8, guide: "Serve and enjoy: Fold or roll up the tortillas, securing the filling. Serve the chicken fajitas immediately while still warm. Feel free to adjust the seasonings and toppings according to your taste preferences. Enjoy your homemade chicken fajitas!")]),
       
       Recipe(title: "Pasta", imageName:"pasta", serving: 3, preparationTime: 60, info: "Pasta is a staple food in Italian cuisine and is enjoyed worldwide. It is typically made from a dough of durum wheat flour or semolina mixed with water or eggs. The dough is kneaded, rolled out, and then cut into various shapes and sizes, resulting in different types of pasta such as spaghetti, penne, fettuccine, or lasagna sheets.",
              ingredients: [Ingredients(name: "Pasta", amount: "450g" ),Ingredients(name: "Meat", amount: "250g" ),Ingredients(name: "Beans", amount: "40g" ), Ingredients(name: "Tomato sauce", amount: "100g" ),Ingredients(name: "Pepper", amount: "20g" ), Ingredients(name: "Vinegar", amount: "1 teaspoon" ), Ingredients(name: "Basil", amount: "30g" ), Ingredients(name: "Cheese", amount: "if desired" ), Ingredients(name: "Olive oil", amount: "150g")],
              cookingGuide: [CookingGuide(step: 1, guide: "Boil water: Fill a large pot with water (generally, 4-6 quarts of water per pound of pasta). Place the pot on the stove over high heat and bring the water to a rolling boil."), CookingGuide(step: 2, guide: "Add salt: Once the water comes to a boil, you can add salt to enhance the flavor of the pasta. Typically, around 1-2 tablespoons of salt per pound of pasta is used. However, adding salt is optional, so you can omit it if desired."), CookingGuide(step: 3, guide: "Add pasta: Carefully add the pasta to the boiling water. Stir gently to prevent the pasta from sticking together or to the bottom of the pot."), CookingGuide(step: 4, guide: "Cook pasta: Follow the instructions on the pasta package for the recommended cooking time. Different types and shapes of pasta have varying cooking times. It is important to cook the pasta until it is 'al dente' which means it is cooked but still has a slight firmness to it. Avoid overcooking the pasta, as it can become mushy"), CookingGuide(step: 5, guide: "Stir occasionally: While the pasta is cooking, stir it occasionally to ensure even cooking and to prevent sticking. Use a long-handled spoon or pasta fork to gently stir the pasta."), CookingGuide(step: 6, guide: "Test for doneness: To check if the pasta is cooked to your desired texture, carefully remove a small piece and taste it. It should be firm but not hard in the center. Keep in mind that pasta will continue to cook slightly after being drained, so it's better to slightly undercook it if you prefer a more al dente texture."), CookingGuide(step: 7, guide: "Drain the pasta: Once the pasta is cooked to your liking, turn off the heat and carefully pour the contents of the pot into a colander or sieve to drain the water. Shake the colander gently to remove excess water."), CookingGuide(step: 8, guide: "Rinse (optional): Most often, pasta is not rinsed after draining, especially when using a sauce. However, for certain pasta dishes like cold pasta salads, rinsing the pasta under cold water can help stop the cooking process and cool it down."), CookingGuide(step: 9, guide: "Serve: Transfer the drained pasta to a serving dish or individual plates. Add your preferred sauce or toppings, such as marinara sauce, Alfredo sauce, or sautéed vegetables and meat. Toss the pasta gently to coat it evenly with the sauce or toppings."), CookingGuide(step: 10, guide: "Enjoy: Serve the pasta immediately while it is still hot, and enjoy your delicious meal.")]),
       
       Recipe(title: "Lasagna", imageName:"lasagna", serving: 4, preparationTime: 120, info: "Lasagna is a classic Italian dish that consists of layers of wide, flat pasta sheets, sauce, cheese, and often includes meat, vegetables, and herbs. It is typically baked in the oven until the layers meld together and the cheese on top becomes melted and golden.",
              ingredients: [Ingredients(name: "Mushroom", amount: "300g" ),Ingredients(name: "Chicken", amount: "500g" ),Ingredients(name: "Coriander", amount: "as desired" ), Ingredients(name: "Tomato", amount: "300g" ),Ingredients(name: "Olive oil", amount: "150g" ), Ingredients(name: "Pasta sheets", amount: "20 dona" ), Ingredients(name: "Saalt", amount: "1 tablesppon" ), Ingredients(name: "Cheese", amount: "200g" ), Ingredients(name: "Cream", amount: "40g"), Ingredients(name: "Milk", amount: "800ml"), Ingredients(name: "Pepper", amount: "1 teaspoon")],
              cookingGuide: [CookingGuide(step: 1, guide: "Preheat the oven: Preheat your oven to 375°F (190°C)."), CookingGuide(step: 2, guide:"Cook the lasagna noodles: Bring a large pot of salted water to a boil. Add the lasagna noodles and cook according to the package instructions until al dente. Drain the noodles and rinse them with cold water to stop the cooking process. Set aside."), CookingGuide(step: 3, guide: "Prepare the meat sauce: In a large skillet or saucepan, heat some olive oil over medium heat. Add the chopped onion and minced garlic, and cook until the onion becomes translucent and the garlic is fragrant. Add the ground beef or Italian sausage, breaking it up with a spoon, and cook until it's browned and cooked through. Drain any excess fat. Add the tomato sauce or marinara sauce to the skillet, along with salt, pepper, and any additional seasonings you prefer. Let the sauce simmer for about 10-15 minutes to allow the flavors to meld together."), CookingGuide(step: 4, guide: "Prepare the ricotta filling: In a bowl, combine the ricotta cheese with grated Parmesan cheese. Season with salt, pepper, and chopped fresh basil leaves (if using). Mix well to incorporate the ingredients."), CookingGuide(step: 5, guide: "Assemble the lasagna: In a baking dish, spread a thin layer of meat sauce on the bottom. Place a layer of cooked lasagna noodles over the sauce, slightly overlapping them. Spread a layer of the ricotta mixture over the noodles, followed by a layer of meat sauce. Sprinkle a layer of shredded mozzarella cheese on top. Repeat the layering process until all the ingredients are used, finishing with a layer of meat sauce and a generous sprinkle of mozzarella cheese."), CookingGuide(step: 6, guide: "Bake the lasagna: Cover the baking dish with aluminum foil and bake in the preheated oven for about 25 minutes. Then, remove the foil and continue baking for an additional 15-20 minutes or until the cheese is melted and bubbly, and the lasagna is heated through."), CookingGuide(step: 7, guide: "Rest and serve: Once the lasagna is done baking, remove it from the oven and let it rest for about 10 minutes. This allows the lasagna to set and makes it easier to slice and serve. Garnish with fresh basil leaves, if desired."), CookingGuide(step: 8, guide: "Slice and enjoy: Cut the lasagna into portions and serve while still warm. It pairs well with a side salad or garlic bread. Enjoy your delicious homemade lasagna!")]),
       
       Recipe(title: "Hot dog", imageName:"hot-dog", serving: 7, preparationTime: 90, info: "Hot dogs are a popular food item that consists of a cooked sausage served in a sliced bun. They are commonly enjoyed at barbecues, sporting events, picnics, and as a quick and easy meal. Hot dogs can be customized with various toppings and condiments to enhance their flavor.",
              ingredients: [Ingredients(name: "Sausage", amount: "10" ),Ingredients(name: "Hot dog buns", amount: "10 pieces" ), Ingredients(name: "Mayonnaise", amount: "40g" ), Ingredients(name: "Mustard", amount: "50g"), Ingredients(name: "Relish", amount: "50g"), Ingredients(name: "Cheese", amount: "80g"), Ingredients(name: "Ketchup", amount: "100g"), Ingredients(name: "Pomidor", amount: "3"), Ingredients(name: "Onion", amount: "1"), Ingredients(name: "Chili", amount: "100g")],
              cookingGuide: [CookingGuide(step: 1, guide: "Cook the sausages: There are different methods for cooking hot dog sausages. You can grill them on a barbecue, cook them on a stovetop griddle or skillet, or even boil them in water."), CookingGuide(step: 2, guide: "Grilling: Preheat your grill to medium-high heat. Place the hot dog sausages on the grill and cook for about 5-7 minutes, turning occasionally, until they are heated through and have grill marks."), CookingGuide(step: 3, guide: "Stovetop: Heat a skillet or griddle over medium heat. Add the sausages and cook for about 5-7 minutes, turning occasionally, until they are heated through and nicely browned."), CookingGuide(step: 4, guide: "Boiling: Bring a pot of water to a simmer. Add the sausages and cook for about 5 minutes or until they are heated through. This method is commonly used for precooked sausages."), CookingGuide(step: 5, guide: "Prepare the buns: While the sausages are cooking, prepare the hot dog buns. Slice each bun lengthwise, leaving a hinge at one end to hold the sausage."), CookingGuide(step: 6, guide: "Assemble the hot dogs: Once the sausages are cooked, place them inside the buns. Add your desired toppings and condiments. Some popular options include ketchup, mustard, mayonnaise, relish, onions, sauerkraut, cheese, chili, or any other toppings you enjoy."), CookingGuide(step: 7, guide: "Serve and enjoy: Serve the hot dogs immediately while they are still hot. You can enjoy them as is or accompany them with side dishes like french fries, coleslaw, or a fresh salad.")])
